import React, { useState } from "react";

// const [content, setContent] = useState("");
// const [title, setTitle] = useState("");

function CreateArea(props) {
  // create a state to keep track of the content and title
  const [notes, setNotes] = useState({
    title: "",
    content: "",
  });

  // a function that handel/save the event of the user input
  function handelChange(event) {
    const { name, value } = event.target;

    setNotes((prevnote) => {
      return { ...prevnote, [name]: value };
    });

    // if (name === "title") {
    //   setTitle(value);
    // } else if (name === "content") {
    //   setContent(value);
    // }
  }
// a function to pass the data to the app
  function notetext(event) {
    event.preventDefault();

    //passing the function from the app using props
    props.onChange(notes);

    // clear the input 
    setNotes({
      title: "",
      content: "",
    });
  }

  return (
    <div>
      <form>
        <input
          onChange={handelChange}
          name="title"
          placeholder="Title"
          value={notes.title}
        />
        <textarea
          onChange={handelChange}
          name="content"
          placeholder="Take a note..."
          rows="3"
          value={notes.content}
        />
        <button onClick={notetext}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
